     */
    public function run()
    {
        //Data Type
        $dataType = $this->dataType('slug', 'news');
//        if (!$dataType->exists) {
            $dataType->fill([
                'name'                  => 'news',
                'display_name_singular' => 'News',
                'display_name_plural'   => 'News',
                'icon'                  => 'voyager-news',
                'model_name'            => 'App\\Models\\News',
                'controller'            => 'App\\Http\\Controllers\\Admin\\NewsController',
