        //Menu Item
        $menu = Menu::where('name', 'admin')->firstOrFail();
        $newsMenuItem = MenuItem::firstOrNew([
            'menu_id' => $menu->id,
            'title'   => 'News',
            'url'     => '',
            'route'   => null
        ]);
        $menuItem = MenuItem::firstOrNew([
            'menu_id' => $menu->id,
            'title'   => 'List',
            'url'     => '/admin/news',
            'route'   => 'voyager.news.index',
        ]);
        if (!$menuItem->exists) {
            $menuItem->fill([
                'target'     => '_self',
                'icon_class' => 'voyager-news',
                'color'      => null,
                'parent_id'  => $newsMenuItem->id,
                'order'      => 1,
            ])->save();
